sys_script_include.script:
var UserLoginUtils = Class.create();
UserLoginUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    validateLogin: function () {
        var username = this.getParameter('sysparm_username');
        var password = this.getParameter('sysparm_password');

        var gr = new GlideRecord('x_1777595_first_0_user');
        gr.addQuery('username', username);
        gr.addQuery('password', password);
        gr.setLimit(1);
        gr.query();

        if (gr.next()) {
            return "success";
        } else {
            return "failure";
        }

    },
	type : 'UserLoginUtils'
});
