sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {

    var state = event.payload.item.id;
    console.log(JSON.stringify(state));
    state = state.replace('l', '');
    var query = api.state.filterQuery;

    if (query == "" || query==null) {
      query = "state="+state;
    } else {
        if(query.includes("state")){
            query = query.replace(/state=\d+/, "state="+state);
        }else{
            query = query + "^state="+state;
        }
    }
    api.setState("filterQuery",query);
    console.log(JSON.stringify(query));

}
