sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    console.log(JSON.stringify(event.payload, null, 4));

    var queryFromState = api.state.usersQuery;
    var titleFromState = api.state.usersTitle;

    var passedQuery = event.payload.heListTitle;

    if (passedQuery == "one") {
        api.setState('usersQuery', "active=True");
        api.setState('usersTitle', "All Active Users");
    } else {
      api.setState('usersQuery', "active=False");
      api.setState('usersTitle', "All In Active Users");
    }





}
