sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {

    var priority = event.payload.item.id;
    console.log(JSON.stringify(priority));
    priority = priority.replace('p', '');
    var query = api.state.filterQuery;

    if (query == "" || query==null) {
      query = "priority="+priority;
    } else {
        if(query.includes("priority")){
            query = query.replace(/priority=\d+/, "priority="+priority);
        }else{
            query = query + "^priority="+priority;
        }
    }
    api.setState("filterQuery",query);
    console.log(JSON.stringify(query));
}
